// Enhanced Motive A1 Audio App with Better Design

import { useState } from "react";
import { pdfjs } from "react-pdf";
import { Document, Page } from "react-pdf";
import "react-pdf/dist/esm/Page/AnnotationLayer.css";
import { Howl } from "howler";
import audioMap from "./audioMap";
import "./index.css";

// Set PDF.js worker path
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

export default function App() {
  const [numPages, setNumPages] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const scale = 1.4;

  const handlePlay = (audioSrc) => {
    const sound = new Howl({ src: [audioSrc] });
    sound.play();
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>Motive A1 Kursbuch</h1>
        <p>Read and listen interactively</p>
      </header>



      <div className="nav-controls">
        <button onClick={() => setCurrentPage(p => Math.max(p - 1, 1))}>
          â¬… Prev
        </button>
        <span>Page {currentPage} / {numPages}</span>
        <button onClick={() => setCurrentPage(p => Math.min(p + 1, numPages))}>
          Next âž¡
        </button>
      </div>

      <div className="pdf-viewer">
        <Document
          file="/motive-a1-kursbuch-1.pdf"
          onLoadSuccess={({ numPages }) => setNumPages(numPages)}
        >
          <Page
            pageNumber={currentPage}
            scale={1.0}
            renderTextLayer={false}
            renderAnnotationLayer={false}
          />
        </Document>

        {audioMap.filter(a => a.page === currentPage).map((btn, idx) => (
          <button
            key={idx}
            className="audio-btn"
            style={{ top: btn.y, left: btn.x }}
            onClick={() => handlePlay(btn.audio)}
          >
            ðŸ”Š {btn.label}
          </button>
        ))}
      </div>
    </div>
  );
}
