import { useEffect, useRef, useState } from "react";
import { pdfjs } from "react-pdf";
import { Document, Page } from "react-pdf";
import "react-pdf/dist/esm/Page/AnnotationLayer.css";
import { Howl } from "howler";

import "./index.css";


// Set PDF.js worker path
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

// Sample mapping of audio buttons to pages and positions
const audioMap = [
  {
    page: 5,
    x: 150,
    y: 300,
    audio: "/audio/cd1_03_Lektion_00_1c.mp3",
    label: "HÃ¶ren 1c"
  },
  {
    page: 6,
    x: 200,
    y: 350,
    audio: "/audio/cd1_04_Lektion_00_2a.mp3",
    label: "HÃ¶ren 2a"
  }
];

export default function App() {
  const [numPages, setNumPages] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [scale] = useState(1.5);

  const handlePlay = (audioSrc) => {
    const sound = new Howl({ src: [audioSrc] });
    sound.play();
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Motive A1 Kursbuch with Audio</h1>
      <div className="border p-2 relative">
        <Document
          file="/motive-a1-kursbuch-1.pdf"
          onLoadSuccess={({ numPages }) => setNumPages(numPages)}
        >
          <Page pageNumber={currentPage} scale={scale} />
        </Document>
        {audioMap
          .filter((item) => item.page === currentPage)
          .map((item, idx) => (
            <button
              key={idx}
              className="absolute bg-white border rounded-full p-2 shadow text-sm"
              style={{ left: item.x, top: item.y }}
              onClick={() => handlePlay(item.audio)}
            >
              ðŸ”Š {item.label}
            </button>
          ))}
      </div>

      <div className="mt-4 flex gap-2">
        <button
          onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}
          className="px-4 py-2 bg-blue-500 text-white rounded"
        >
          Prev
        </button>
        <button
          onClick={() => setCurrentPage((p) => Math.min(p + 1, numPages))}
          className="px-4 py-2 bg-blue-500 text-white rounded"
        >
          Next
        </button>
        <span>Page {currentPage} / {numPages}</span>
      </div>
    </div>
  );
}
